#BLAST de nucleotidos, usando a base de dados nt
blast = NCBIWWW.qblast("blastn", "nt", phb2fa.format("fasta"))

save_file = open("my_blast_phb2.xml","w")
save_file.write(blast.read())
save_file.close()
blast.close()

